-- Eras
--------------------------------------------------------------
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_01_Ancient.dds',			EraCivicBackgroundTexture='CivicBG_Grey_01_Ancient.dds'			WHERE EraType='ERA_ANCIENT';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_02_Classical.dds',		EraCivicBackgroundTexture='CivicBG_Grey_02_Classical.dds'		WHERE EraType='ERA_CLASSICAL';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_03_PostClassical.dds',	EraCivicBackgroundTexture='CivicBG_Grey_03_PostClassical.dds'	WHERE EraType='ERA_6T_POST_CLASSICAL';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_04_Medieval.dds',			EraCivicBackgroundTexture='CivicBG_Grey_04_Medieval.dds'		WHERE EraType='ERA_MEDIEVAL';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_05_Renaissance.dds',		EraCivicBackgroundTexture='CivicBG_Grey_05_Renaissance.dds'		WHERE EraType='ERA_RENAISSANCE';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_06_Industrial.dds',		EraCivicBackgroundTexture='CivicBG_Grey_06_Industrial.dds'		WHERE EraType='ERA_INDUSTRIAL';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_07_Modern.dds',			EraCivicBackgroundTexture='CivicBG_Grey_07_Modern.dds'			WHERE EraType='ERA_MODERN';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_08_Atomic.dds',			EraCivicBackgroundTexture='CivicBG_Grey_08_Atomic.dds'			WHERE EraType='ERA_ATOMIC';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_09_Information.dds',		EraCivicBackgroundTexture='CivicBG_Grey_09_Information.dds'		WHERE EraType='ERA_INFORMATION';
UPDATE Eras SET EraTechBackgroundTexture='TechBG_Grey_10_Future.dds',			EraCivicBackgroundTexture='CivicBG_Grey_10_Future.dds'			WHERE EraType='ERA_FUTURE';
