INSERT OR IGNORE INTO LocalizedText
		(Language,		Tag,																							Text)
VALUES	('ru_RU',		'LOC_GREATPERSON_COMBAT_STRENGTH_AOE_ANCIENT_LAND',												'+5 [ICON_Strength] Силы и +1 [ICON_Movement] Движения для наземных юнитов эпохи Античности и Классической эпохи в пределах 2 клеток.'),

		('ru_RU',		'LOC_GREAT_PERSON_INDIVIDUAL_JNR_FU_HAO_NAME',													'Фу Хао'),
		('ru_RU',		'LOC_JNR_FU_HAO_FREE_UNIT',																		'Создает кавалерийский юнит, который может быть обнаружен только соседними вражескими юнитами.'),
		('ru_RU',		'LOC_PEDIA_GREATPEOPLE_PAGE_GREAT_PERSON_INDIVIDUAL_JNR_FU_HAO_CHAPTER_HISTORY_PARA_1',			'Фу Хао (посмертное храмовое имя Му Син) была одной из многочисленных жен короля У Дина из династии Шан, а также служила военным генералом и верховной жрицей. Она умерла около 1200 года до н.э. Гробница Фу Хао была обнаружена в Иньсю нетронутой и содержала множество сокровищ, включая более 700 нефритовых предметов, многие из которых были антикварными, и более 500 костяных предметов, таких как оракулы из костей, используемые в её ритуалах.'),
		('ru_RU',		'LOC_PEDIA_GREATPEOPLE_PAGE_GREAT_PERSON_INDIVIDUAL_JNR_FU_HAO_CHAPTER_HISTORY_PARA_2',			'Фу Хао вошла в королевскую семью через брак и использовала полу-матриархальное устройство рабовладельческого общества, чтобы подняться в статусе и стать одной из трёх жен короля У Дина. Есть предположение, что она была родом из приграничных районов или степей, что подтверждается найденным оружием в её гробнице.'),
		('ru_RU',		'LOC_PEDIA_GREATPEOPLE_PAGE_GREAT_PERSON_INDIVIDUAL_JNR_FU_HAO_CHAPTER_HISTORY_PARA_3',			'В своей военной роли она отвечала за покорение врагов и соседей династии Шан. Ту-Фан, которые вели борьбу с Шан на протяжении многих поколений, были окончательно побеждены Фу Хао в решающей битве. За этим последовали дальнейшие кампании против соседних племен И, Цян и Ба. Последняя кампания особенно известна как первая крупная засадная операция в истории Китая. С армией до 13 000 солдат и важными генералами Чжи и Хоу Гао под её командованием она была самым могущественным генералом Шан своего времени.'),

		('ru_RU',		'LOC_GREAT_PERSON_INDIVIDUAL_JNR_SIYAJ_K_AK_NAME',												'Сиях К''ак'''),
		('ru_RU',		'LOC_PEDIA_GREATPEOPLE_PAGE_GREAT_PERSON_INDIVIDUAL_JNR_SIYAJ_K_AK_CHAPTER_HISTORY_PARA_1',		'Сиях К''ак'', также известный как "Рождённый Огнём", был выдающейся политической фигурой, упоминаемой в глифах классической эпохи цивилизации Майя (250–800 гг. н.э.), особенно в Тикале (захваченного им в январе 378 года), а также в Уакшактун и городе Копан. Предполагается, что он был генералом правителя Теотиуакана Копьеметателя Совы.'),
		('ru_RU',		'LOC_PEDIA_GREATPEOPLE_PAGE_GREAT_PERSON_INDIVIDUAL_JNR_SIYAJ_K_AK_CHAPTER_HISTORY_PARA_2',		'Родом из Теотиуакана или тесно связанный с этим городом, Сиях К''ак'' был военным лидером в сердце Майя (современная Гватемала) в четвёртом веке. В 378 и 379 годах он организовал замену правителей в важных городах-государствах Майя, таких как Тикаль, Уакшактун и Копан, новыми правителями, которые заявляли о своём происхождении от Копьеметателя Совы.'),
		('ru_RU',		'LOC_PEDIA_GREATPEOPLE_PAGE_GREAT_PERSON_INDIVIDUAL_JNR_SIYAJ_K_AK_CHAPTER_HISTORY_PARA_3',		'Сиях К''ак'' оставался влиятельной фигурой в регионе до своей смерти в начале пятого века, действуя как повелитель таких правителей, как Яш Нун Айин I ("Курносый") из Тикаля. Его деятельность ознаменовала начало мощного культурного влияния из долины Мексики, что в частности выразилось в использовании архитектурного стиля талуд-таблеро, типичного для мексиканских нагорий, но необычного для Майя.').
