INSERT OR REPLACE INTO LocalizedText
		(Language, Tag,	Text)
VALUES

		('ru_RU',
		'LOC_BUILDING_LEU_SUEZ_CANAL_NAME',
		'Суэцкий канал'),
		
		('ru_RU', 
		'LOC_BUILDING_LEU_SUEZ_CANAL_QUOTE',					
		'«Я наблюдаю волшебные миражи рек и лугов;[NEWLINE]Бегут на протяжении трех или четырех тысяч миль пути,[NEWLINE]Связывая Восток с Западным морем,[NEWLINE]Как Суэцкий канал связал Европу с Азией».[NEWLINE]— Уолт Уитмен');

INSERT OR REPLACE INTO LocalizedText
		(Language, Tag,	Text)
VALUES
		("ru_RU", 
		"LOC_BUILDING_LEU_SUEZ_CANAL_DESC",					
		"По окончании строительства автоматически создается 1 или 2 канала на соседних клетках. По клетке с этим чудом света могут проходить морские юниты.[NEWLINE][NEWLINE]Можно построить только на равнинных клетках. С разных сторон от чуда должно быть две клетки: одна должна соответствовать требованиям для постройки канала, примыкающего к Суэцкому каналу, а вторая должна быть городской или водной — или клеткой для ещё одного примыкающего канала. [NEWLINE][NEWLINE]Каждый новый построенный канал даёт +1 [ICON_TRADEROUTE] доступный торговый путь, включая каналов, созданные для этого чуда.[NEWLINE][NEWLINE]Пока вы не ведете войну с другими цивилизациями, каждый [ICON_TRADEROUTE] торговый путь, отправленный в город-государство, дает 1 [ICON_ENVOY] посла.[NEWLINE][NEWLINE]+1 [ICON_FAVOR] мировое влияние."),

		("ru_RU",
		"LOC_PEDIA_WONDERS_PAGE_BUILDING_LEU_SUEZ_CANAL_CHAPTER_HISTORY_PARA_1",
		"Суэцкий канал — это морской водоем в Египте, соединяющий Средиземное и Красное моря через Суэцкий перешеек и разделяющий Африку и Азию. В 1858 году Фердинанд де Лессепс создал «Компания Суэцкого канала» с прямой целью строительства канала. Строительство канала продолжалось с 1859 по 1869 год, и 17 ноября 1869 года состоялось торжественное открытие канала, в котором приняли участие императрица Франции Евгения, Исмаил-паша Египта и Судана и император Франц Иосиф I. В 1870 году, в первый полный год работы канала, было совершено 486 транзитов, или менее 2 в день. В 1966 году их было 21 250, в среднем 58 в день. В 2018 году было совершено 18 174 транзита, а чистый годовой тоннаж составил около 1 139 630 000 метрических тонн. Первоначально канал не допускал двустороннего движения, и суда останавливались в проходной бухте, чтобы дать возможность пройти судам в другом направлении."),

		("ru_RU",
		"LOC_PEDIA_WONDERS_PAGE_BUILDING_LEU_SUEZ_CANAL_CHAPTER_HISTORY_PARA_2",
		"Канал оказал немедленное и драматическое влияние на мировую торговлю. В сочетании с американской трансконтинентальной железной дорогой, строительство которой было завершено шестью месяцами ранее, он позволил обогнуть весь мир за рекордно короткое время. Он сыграл важную роль в расширении европейской колонизации Африки. Строительство канала стало одной из причин паники 1873 года в Великобритании, поскольку до этого товары с Дальнего Востока перевозились на парусных судах вокруг мыса Доброй Надежды и хранились на британских складах."),

		("ru_RU",
		"LOC_PEDIA_WONDERS_PAGE_BUILDING_LEU_SUEZ_CANAL_CHAPTER_HISTORY_PARA_3",
		"В 1951 году Египет расторг договор 1936 года с Великобританией. В октябре 1954 года Великобритания предварительно согласилась вывести свои войска из зоны канала. В связи с тем, что Египет сделал шаг навстречу Советскому Союзу, Великобритания и США отозвали свое обещание оказать финансовую поддержку строительству Асуанской плотины. В ответ на это президент Египта Гамаль Абдель Насер 26 июля 1956 года национализировал канал и передал его администрации Суэцкого канала, намереваясь финансировать проект плотины за счет доходов от канала. В тот же день, когда канал был национализирован, Насер также закрыл Тиранский пролив для всех израильских судов. Это привело к Суэцкому кризису, в ходе которого Великобритания, Франция и Израиль вторглись в Египет. В соответствии с заранее согласованными планами военных действий по Суэцкому протоколу, 29 октября Израиль вторгся на Синайский полуостров, вынудив Египет вступить с ним в военные действия, что позволило англо-французскому партнерству объявить возникшие боевые действия угрозой стабильности на Ближнем Востоке и вступить в войну, официально для разделения двух сил, но на самом деле для восстановления канала и свержения правительства Насера."),

		("ru_RU",
		"LOC_PEDIA_WONDERS_PAGE_BUILDING_LEU_SUEZ_CANAL_CHAPTER_HISTORY_PARA_4",
		"Экзотические виды из Индо-Тихоокеанской области мирового океана, завезенные в Средиземноморье через канал с 1880-х годов, стали значительным компонентом экосистемы Средиземноморья. Они уже влияют на ее экологию, подвергая опасности некоторые местные и эндемичные виды. С момента прокладки канала в Средиземноморье было зарегистрировано более тысячи видов из Красного моря — планктон, морские водоросли, беспозвоночные, рыбы, и многие другие, очевидно, последуют за ними. Изменения в биоразнообразии не имеют прецедентов на памяти человечества и ускоряются."),

		("ru_RU",
		"LOC_PEDIA_WONDERS_PAGE_BUILDING_LEU_SUEZ_CANAL_CHAPTER_HISTORY_PARA_5",
		"23 марта 2021 года Суэцкий канал был заблокирован в обоих направлениях сверхкрупным контейнеровозом Evergreen G-класса «Ever Given». Судно, управляемое компанией Evergreen Marine, направлялось из Малайзии в Нидерланды, когда село на мель из-за сильного ветра, предположительно сбившего судно с курса. Сев на мель, «Ever Given» перевернулся на бок, полностью перекрыв канал. Несмотря на то, что на части длины канала имеется старый более узкий канал, который можно использовать для обхода препятствий, данный инцидент произошел к югу от этого места, на участке канала, где имеется только один канал. Когда инцидент начался, многие экономисты и торговые эксперты говорили о последствиях препятствия, если оно не будет быстро устранено, ссылаясь на то, насколько важен Суэцкий канал для мировой торговли. Судно было вновь спущено на воду 29 марта. Уже через несколько часов грузоперевозки возобновились, постепенно устраняя отставание от графика, составлявшее около 450 судов.");



