INSERT INTO LocalizedText
	(Language,		Tag,																		Text)
VALUES	('ru_RU',		'LOC_BUILDING_JNR_TELEFERICO_NAME',											'Эль-Альто'),
		('ru_RU',		'LOC_BUILDING_JNR_TELEFERICO_DESCRIPTION',									'Все города получают +2 [ICON_HOUSING] Жилищ, +1 [ICON_AMENITIES] Удобство и +5% роста за каждый район, расположенный рядом с горой.[NEWLINE][NEWLINE]Должен быть построен на горной клетке, которая не содержит вулкана.'),
		('ru_RU',		'LOC_BUILDING_JNR_TELEFERICO_QUOTE',										'«Мы не Олимп или Акрополь, мы место и сцена преобразований, порожденных трудом, а не только словами.»[NEWLINE] – Луис Раймундо Киспэ Флорес.'),
		('ru_RU',		'LOC_PEDIA_WONDERS_PAGE_BUILDING_JNR_TELEFERICO_CHAPTER_HISTORY_PARA_1',	'Город Эль-Альто ("Высокий") — второй по величине город в Боливии и бывший район Ла-Паса, над которым он расположен на плато, ранее населенном народом аймара. Эль-Альто начался как железнодорожный терминал для Ла-Паса и превратился в крупный промышленный район, став домом для самого высокого аэропорта в мире. Крутые склоны делают невозможным использование рельсового или автомобильного общественного транспорта по прямому маршруту, и теперь два города связаны самой большой в мире системой общественного канатного транспорта «Mi Teleférico», аналогичные решения приняты вдоль всех Андских гор. Эль-Альто стал известен своим экстравагантным нео-андским архитектурным стилем, который характеризует яркие шале нового класса богатых коренных предпринимателей аймара, которые устремились в Эль-Альто, чтобы заниматься торговлей и другими коммерческими видами деятельности. Нео-андский (или нео-тиуанакотанский) архитектурный стиль сочетает в себе местные влияния, такие как формы и узоры из древнего города Тиуанаку и цвета одежды аймара, с модернистскими конструкциями из стекла и бетона. В последнее время они также стали известны за рубежом как «дома-трансформеры» благодаря более широкому спектру тем, включая элементы из современной фантастики. Дома часто включают бальные залы и другие удобства для проведения крупных мероприятий и социальных собраний, привлекая туристов со всего мира.');


-- Simplified Chinese
--------------------------------------------------------------
INSERT INTO LocalizedText
		(Language,		Tag,																		Text)
VALUES	('zh_Hans_CN',	'LOC_BUILDING_JNR_TELEFERICO_NAME',											'埃尔阿尔托'),
		('zh_Hans_CN',	'LOC_BUILDING_JNR_TELEFERICO_DESCRIPTION',									'所有相邻山脉的区域提供+1 [ICON_Amenities] 宜居度、+2 [ICON_Housing] 住房和+5%城市发展速度。必须建造在无火山的山脉上。'),
		('zh_Hans_CN',	'LOC_BUILDING_JNR_TELEFERICO_QUOTE',										'这并非奥林匹斯山或者卫城，这是通过工作而非空谈来促进变革的场所和舞台。[NEWLINE]——路易斯·雷蒙多·基斯佩·弗洛雷斯'),
		('zh_Hans_CN',	'LOC_PEDIA_WONDERS_PAGE_BUILDING_JNR_TELEFERICO_CHAPTER_HISTORY_PARA_1',	'埃尔阿尔托市（“高原”）是玻利维亚的第二大城市，曾是拉巴斯的一个区，位于拉巴斯之上的高原上，以前是艾马拉人居住的地方。埃尔阿尔托最初是拉巴斯的铁路终点站，后来发展成为一个大型工业区，并最终成为世界上海拔最高的机场所在地。陡峭的山坡使得铁路或公路公共交通难以直达，现在这两座城市根据沿安第斯山脉类似城市的方案，通过世界上最大的缆车公共交通系统“Mi Teleférico”连接起来。艾马拉原住民企业家纷至沓来，在艾尔阿尔托从事贸易和其他商业活动，这个新的阶层建造色彩鲜豔的小木屋，形成埃尔阿尔托遐迩的奢华新安第斯建筑风格。新安第斯（或新蒂瓦纳科坦）建筑将蒂瓦纳科古城的形状和图案、艾马拉服饰的色彩等传统影响结合于玻璃和混凝土的现代主义建筑。最近，这些房屋在海外被称为“变形金刚房屋”，因为它们融入了更广泛的主题，甚至从当代作品中汲取灵感。这些房屋通常设有舞厅和其他设施，用于举办大型活动和其他社交聚会，甚至吸引了来自世界各地的游客。');
